import './AuthForm.css'
import { useState } from "react";

function SignupForm(props) {
    const [user, setUser] = useState({
        username: '',
        email: '',
        password1: '',
        password2: '',
    })

    const handleError = (err) => {
        console.warm(err)
    }

    const handleInput = (e) => {
        const {name, value} = e.target;
        setUser(prev => ({
            ...prev,
            [name]: value,
        }))
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        // testing functionality
        console.log("User info:", user)

        // uncomment for build
        // const options = {
        //     method: "POST",
