        //     },
        //     body: JSON.stringify(user),
        // }
        // const response = await fetch('api_v1/dj-rest-auth/login/', options)
        // const data = await response.json()
        // console.log("login data: ", data)
        // Cookies.set("Authorization", `Token ${data.key}`)
    }


    return (
        <div id="form-container">
            <form id="auth-form" >
                <div className="form-group">
                    <label htmlFor="username">Username</label>
                    <input type="text" className="form-control" id="username" name="username" value={user.username} onChange={handleInput} placeholder="xX_C00L-n4m3_Xx"/>
                </div>
                <div className="form-group">
                    <label htmlFor="email1">Email address</label>
                    <input type="email" className="form-control" id="email" name="email" aria-describedby="emailHelp" placeholder="Enter email"/>
                    <small id="emailHelp" className="form-text text-muted">We'll never share your email with anyone else.</small>
                </div>
                <div className="form-group">
                    <label htmlFor="password1">Password</label>
                    <input type="password" className="form-control" id="password1" name="password1" value={user.password1} onChange={handleInput} placeholder="Secure Password"/>
                </div>
                <div className="form-group">
                    <label htmlFor="password2">Confirm Password</label>
                    <input type="password" className="form-control" id="password2" name="password2" value={user.password2} onChange={handleInput} placeholder="Confirm Password"/>
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </div>
    )
}

export default SignupForm
