import "./App.css";
import { useState, useEffect } from "react";
import CateNav from "./Components/CateNav";
import ArticleList from "./Components/ArticleList";
import ArticleView from "./Components/ArticleView";

const INITIAL_ARTICLE = {
  title: "Article Title",
  text: "Article text"
}

function App() {
  const [currArticle, setCurrArticle] = useState(INITIAL_ARTICLE);
  const [newArticle, setNewArticle] = useState();
  const [currCategory, setCurrCategory] = useState('all');

  useEffect(() => {
    console.log("new category:", currCategory)
  },[currCategory])

  useEffect(() => {
    const getArticles = async (e) => {
      const options = {
        method: "GET",
        headers: {
          "Content-Type": "application/json"
        },
      }
      const response = await fetch(`api_v1/posts/${newArticle}`, options)
      const data = await response.json()
      setCurrArticle(data)
    }
    getArticles()
  }, [newArticle])


	return (
		<div className="app-body">
		  <header className="App-header">
        <CateNav setCurrCategory={setCurrCategory} />
      </header>
      <main>
        <ArticleList currCategory={currCategory} setNewArticle={setNewArticle} />
        <ArticleView article={currArticle} />
      </main>
		</div>
	);
}

export default App;
