import {useState, useEffect} from 'react'
import { nanoid } from 'nanoid'
import styled from 'styled-components'
import CateTab from './CateTab'
import './cate-nav.css'

// # CATEGORIES
//     BUSINESS = 'bus'
//     GAMING = 'game'
//     HEALTH = 'hlth'
//     POLITICS = 'pol'
//     WORLD = 'wrld'

const INITIAL_CATES = {
    Business: {
        name: "Business",
        tag: "bus"
    },
    Gaming: {
        name: "Gaming",
        tag: "game"
    },
    Health: {
        name: "Health",
        tag: "hlth"
    },
    Politics: {
        name: "Politics",
        tag: "pol"
    },
    World: {
        name: "World",
        tag: "wrld"
    },
    'Gaming': 'game',
    'Health': 'hlth',
    'Politics': 'pol',
    'World': 'wrld',
}


function CateNav(props) {
    const [cates, setCates] = useState(INITIAL_CATES);

    const catesHTML = cates.map(cate => (<CateTab cate={cate} key={nanoid()} />))

    return (
        <nav id='cate-nav'>
            {catesHTML}
        </nav>
    )
}

export default CateNav
