import './header.css'
import { Navigate, NavLink, useNavigate, useOutletContext } from "react-router-dom";

function Header(props) {

    const signOut = async (e) => {

        const options = {
            method: "POST",
            headers: {
                "Content-type": "application/json",
            },
            body: "",
        }
        const response = await fetch("/dj-rest-auth/logout/", options)
        if (!response.ok) {
            throw new Error("Error when signing out")
        }
        const data = await response.json();
        props.setAuth(false);

    }

	return (
		<header>
            <nav id='header-nav'>
                <NavLink className="header-link" to="/home">Home</NavLink>
                <NavLink className="header-link" to="/login">Login</NavLink>
                <NavLink className="header-link" to="/signup">Sign up</NavLink>
            </nav>
            {props.isAuth
                ? <section className="user-area">
                    <NavLink className="header-link" to="/home">Welcome User</NavLink>
                    <button id='sign-out' onClick={signOut} >Sign out?</button>
                </section>
                : <section className="user-area">
                    <NavLink className="header-link" to="/login">Have an account?</NavLink>
                </section>}
		</header>
	);
}

export default Header;
