import './article-view.css'

function ArticleView(props) {
    return (
        <section id="article-view">
            {props.title ? <h1>{props.title}</h1> : <h1>Select an article</h1>}
            {props.text ? <p>{props.text}</p> : <p></p>}
        </section>
    )
}

export default ArticleView
