import './AuthForm.css'
import { useState } from "react";

function LoginForm(props) {
    const [user, setUser] = useState({
        username: '',
        password: '',
    })

    const handleError = (err) => {
        console.warm(err)
    }

    const handleInput = (e) => {
        const [name, value] = e.target;
        setUser(prev => ({
            ...prev,
            [name]: value,
        }))
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        // testing functionality
        console.log("User info:", user)
    }

    return (
        <form id="auth-form" onSubmit={handleSubmit} >
            <div className="form-group">
                <label htmlFor="username">Username</label>
                <input type="text" className="form-control" id="username" name="username" value={user.username} onChange={handleInput} placeholder="xX_C00L-n4m3_Xx"/>
            </div>
            <div className="form-group">
                <label htmlFor="exampleInputEmail1">Email address</label>
                <input type="email" className="form-control" id="email" name="email" aria-describedby="emailHelp" placeholder="me@awesome.com"/>
                <small id="email" className="form-text text-muted">We'll never share your email with anyone else.</small>
            </div>
            <div className="form-group">
                <label htmlFor="password1">Password</label>
                <input type="password" className="form-control" id="password" name="password" value={user.password} onChange={handleInput} placeholder="Password"/>
            </div>

            <button type="submit" className="btn btn-primary">Submit</button>
        </form>
    )
}

export default LoginForm
