import { useState, useEffect } from "react";
import ArticleLink from "./ArticleLink";
import {nanoid} from 'nanoid'
import './article-list.css'

const INITIAL_ARTICLES = [
    'article 1',
    'article 2',
    'Funny comics',
    'dsytopia',
]

function ArticleList(props) {
    // Initial load of any posts
    const [articles, setArticles] = useState(INITIAL_ARTICLES)
    useEffect(() => {
        const getArticles = async (e) => {
          const options = {
            method: "GET",
            headers: {
              "Content-Type": "application/json"
            },
          }
          const response = await fetch('api_v1/posts/', options)
          const data = await response.json()
          setArticles(data)
        }
        getArticles()
      }, [])

    const articlesHTML = articles.map(article => (
        <ArticleLink key={nanoid()} article={article} setNewArticle={props.setNewArticle} />
    ))

    return (
        <ul className="link-list">{articlesHTML}</ul>
    )
}

export default ArticleList
