import './AuthForm.css'
import { useState } from "react";

function SignupForm(props) {
    const [user, setUser] = useState({
        username: '',
        email: '',
        password1: '',
        password2: '',
    })

    const handleError = (err) => {
        console.warm(err)
    }

    const handleInput = (e) => {
        const {name, value} = e.target;
        setUser(prev => ({
            ...prev,
            [name]: value,
        }))
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        // testing functionality
        console.log("User info:", user)

        const options = {
            method: "POST",
            headers: {
                "Content-type": "application/json",
                "X-CSRFToken": Cookies.get("csrftoken")
            },
            body: JSON.stringify(user),
        }
        const response = await fetch('api_v1/dj-rest-auth/login/', options)
        const data = await response.json()
        console.log("login data: ", data)
        Cookies.set("Authorization", `Token ${data.key}`)
    }


    return (
        <form id="auth-form" >
            <div class="form-group">
                <label for="username">Username</label>
                <input type="text" class="form-control" id="username" name="username" placeholder="xX_C00l-n4m3_Xx"/>
            </div>
            <div class="form-group">
                <label for="exampleInputEmail1">Email address</label>
                <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email"/>
                <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
            </div>
            <div class="form-group">
                <label for="password1">Password</label>
                <input type="password" class="form-control" id="password1" name="password1" placeholder="Secure Password"/>
            </div>
            <div class="form-group">
                <label for="password2">Confirm Password</label>
                <input type="password" class="form-control" id="password2" name="password2" placeholder="Confirm Password"/>
            </div>
            <button type="submit" class="btn btn-primary">Submit</button>
        </form>
    )
}

export default SignupForm
