import "./App.css";
import { useState } from "react";
import { Outlet } from "react-router-dom";

import Header from "./Header";

function Main() {
	const [isAuth, setAuth] = useState(false);
	return (
		<>
			<Header isAuth={isAuth} />
			<Outlet context={[setAuth]} />
			{/* Outlet changes base on route */}
		</>
	);
}

export default Main;
