import {useState, useEffect} from 'react'
import { nanoid } from 'nanoid'
import styled from 'styled-components'
import CateTab from './CateTab'
import './cate-nav.css'

const INITIAL_CATES = [
    'Business',
    'Gaming',
    'Health',
    'Politics',
    'World',
]


function CateNav(props) {
    const [cates, setCates] = useState(INITIAL_CATES);

    // Fetch categories and build a new CatTab for each
    // useEffect(() => {
    //     const getCates = async (e) => {
    //         const response = await fetch('/api_v1/')
    //     }
    // }, [])

    const catesHTML = cates.map(cat => (<CateTab cate={cat} key={nanoid()} />))

    return (
        <nav id='cate-nav'>
            {catesHTML}
        </nav>
    )
}

export default CateNav
